<script>
    $.ajax({
    url: 'http://api.weatherapi.com/v1/current.json?key=0b8efdce9bbd4d09a1b83814210505&q=cairo&aqi=yes',
    data: {
        access_key: 'e145938931db0ebd48fffb5b6d54ff9b',
        query: 'egypt'
    },
    dataType: 'json',
    success: function(apiResponse) {

        $(".weather-description").append(apiResponse.current.condition.text);
        $(".weather-description").append("<br>"+apiResponse.location.localtime);
        $(".weather-temperature").append(apiResponse.current.feelslike_c);

    }
    });
    $.ajax({
    url: 'https://v6.exchangerate-api.com/v6/4595b566849375118071ce55/latest/USD',
    dataType: 'json',
    success: function(apiResponse) {

        $(".dollar").append(apiResponse.conversion_rates.USD+" دولار ");
        $(".cny").append(apiResponse.conversion_rates.CNY+" يوان صيني ");
        $(".eur").append(apiResponse.conversion_rates.EUR+" يورو ");
        $(".GBP").append(apiResponse.conversion_rates.GBP+" جنيه استرليني ");
        $(".egp").append(apiResponse.conversion_rates.EGP+" جنيه مصري ");
        $(".kwd").append(apiResponse.conversion_rates.KWD+" دينار كويتي ");

    }
    });
</script>
